rowExpressions.expression:
for $feature in $features 
	let $ft := $feature/local-name()
	group by $ft 
	order by $ft
	return 'all; ' || $ft || '; ' || count($feature)

if (count($db)<=1) then () else
for $file in $db
	let $filename := local:filename($file)
	order by $filename
	return
		for $feature in $features[local:filename(.)=$filename]
			let $ft := $feature/local-name()
			group by $ft 
			order by $ft
			return $filename || '; ' || $ft || '; ' || count($feature)

TestAssertion.expression:
(: TODO: extend with subtypes :)
let $nodes := $features[self::*:HydroNode]
return
if (count($nodes) = 0) 
then 'PASSED'
else
let $featuresWithErrors := 
(for $candidate in $nodes
let $candidate_geometry := ggeo:getGeometry($candidate/@gml:id,$candidate/*[self::*:geometry]/*[1])
let $env := ggeo:envelope($candidate_geometry)
let $other_features := ggeo:search($env[1],$env[2],$env[3],$env[4])
let $touches :=
for $feature in $other_features[self::*:WatercourseLink]
let $other_geometry := ggeo:getGeometry($feature/@gml:id,$feature/*:centerlineGeometry/*[1])
return if (ggeo:touches($candidate_geometry,$other_geometry)) then $feature else ()
return if (not($touches)) then $candidate else ())[position() le $limitErrors]
return
(if ($featuresWithErrors) then 'FAILED' else 'PASSED',
 if ($featuresWithErrors) then local:addMessage('featuresWithErrors', map { 'count': string(count($featuresWithErrors)) }) else (),
 for $feature in $featuresWithErrors
   order by $feature/@gml:id
   return
     local:addMessage('freeNode', map { 'filename': local:filename($feature), 'featureType': local-name($feature), 'gmlid': string($feature/@gml:id) }))
