TestAssertion.expression:
let $urlOpengis := 'http://www.opengis.net/def/crs/EPSG/0/'
let $urlCodes := ('http://codes.wmo.int/grib2/codeflag/4.2/_0-3-3')
let $epsgCodes := ('4936','4937','4258','3035','3034','3038','3039','3040','3041','3042','3043','3044','3045','3046','3047','3048','3049','3050','3051','5730','5861','5715','7409')
let $messages :=  		
	(for $record in $records
		let $rid := $record/gmd:fileIdentifier/*/text()
		let $crsIdentifiers := $record/gmd:referenceSystemInfo/gmd:MD_ReferenceSystem/gmd:referenceSystemIdentifier/gmd:RS_Identifier
		return 	 						
			if (count($crsIdentifiers) = 0)  then
			local:addMessage('TR.noCrsIdentifier', map { 'filename': local:filename($record), 'id': $rid })
		else 									
			for $crs in $crsIdentifiers
				let $code := $crs/gmd:code/*[1]/text()
				let $codeSpace := $crs/gmd:codeSpace/*[1]/text()
				let $codeNumber := fn:substring($code,fn:string-length($urlOpengis)+1,4)
				let $invalidCodeValues := $codeNumber[not(. = $epsgCodes)]
				let $invalidCodeValues2 := $code[not(. = $urlCodes)]
	 			return 	 	 												
				if (not(fn:string-length(normalize-space($code)) > 0)) then
					local:addMessage('TR.noCrsCode',  map { 'filename': local:filename($record), 'id': $rid, 'crs': $code })
				else if (($invalidCodeValues) and ($invalidCodeValues2)) then
					local:addMessage('TR.invalidCrsCode',  map { 'filename': local:filename($record), 'id': $rid, 'crs': $code })
				else if ($codeSpace) then
					local:addMessage('TR.invalidCrsCodeSpace',  map { 'filename': local:filename($record), 'id': $rid, 'crs': $code })
				else () 			
	)[position() le $limitErrors] 
return
(if ($messages) then 'FAILED' else 'PASSED',
 local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))),
 $messages)
