	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("version", testSuite.project.getPropertyValue("version"));

	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("request", testSuite.getPropertyValue("defaultGetTile_Request"));
