	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("style", testSuite.getPropertyValue("defaultGetTile_Style"));
	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("format", testSuite.getPropertyValue("defaultGetTile_Format"));
	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("tilematrixset", testSuite.getPropertyValue("defaultGetTile_TileMatrixSet"));
	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("tilematrix", testSuite.getPropertyValue("defaultGetTile_TileMatrix"));
	testRunner.testCase.getTestStepByName("http-request-gettile").setPropertyValue("tilecol", testSuite.getPropertyValue("defaultGetTile_TileCol"));
